import * as React from "react";
import {win} from "../../index";
import {ReactElement, RefObject} from "react";

interface iMousePos{
    x:number;
    y:number;
}

interface iWindow{

}
export class WindowLevelCapture implements iWindow{
    private mousePosition : iMousePos;
    constructor() {
        this.mousePosition = {x:-1000,y:-1000};
    }

    get mousePos(){
        return this.mousePosition;
    }

    getMousePosition(evt:MouseEvent){
        this.mousePosition = { x : evt.clientX, y : evt.clientY};
        //console.log("mouse position", evt.clientX, evt.clientY);

    }

    windowEvent(){
        window.addEventListener('mousemove',
            (evt)=>this.getMousePosition(evt) )
    }
}

interface iPROPS{
    tooltipType? : "standard" | "custom";
    tooltipStandardContent? : {
        headerText : string,
        copy : string
    }
    mouseEntered : boolean;
    toolTipCustomElement? : ReactElement;
    btnReference? : RefObject<HTMLButtonElement>;
    linkReference? : RefObject<HTMLAnchorElement>;

}

interface iSTATE {
    tooltipPosition : {
        x : number,
        y : number
    };
    isHovering : boolean;
    mouseEntered : boolean;
}

export class ToolTip extends React.Component<iPROPS, iSTATE>{
    constructor(props:iPROPS) {
        super(props);
        this.state ={
            tooltipPosition : {x:-window.innerWidth, y:-window.innerHeight},
            isHovering : false,
            mouseEntered : this.props.mouseEntered
        }
    }
    ogRct = this.props.btnReference?.current?.getBoundingClientRect();

    private intervalID : any = 0;

    updateMousePos(){
        //Only update if the mouse is moving, check if position values have changed

        if(this.state.tooltipPosition.x !== win.mousePos.x && this.state.tooltipPosition.y !== win.mousePos.y){
            console.log("check update mouse");
            this.setState(
                {
                    tooltipPosition: {
                        x: win.mousePos.x,
                        y: win.mousePos.y
                    }
                });
            console.log("state changed");
        }

        //this will handle a continuous hover
        if( this.ogRct != undefined ){
            //if the cursor is further to the left of the button, kill it
            if(this.state.tooltipPosition.x <= this.ogRct.x  ){
                this.clearHover();
            }
            //if the cursor is the the right of the button, kill it
            if(this.state.tooltipPosition.x >= this.ogRct.x + this.ogRct.width){
                this.clearHover();
            }
            // //if the cursor is above the button, kill it
            if(this.ogRct.y <= this.state.tooltipPosition.y ){
                this.clearHover();
            }
            // //if this cursor is below the button, kill it
            if(this.state.tooltipPosition.y >= this.ogRct.y + this.ogRct.height){
                this.clearHover();
            }
        }
    }

    getToolTip(){

        if(this.state.isHovering === false){
            return (<></>);
        }

        let tooltipInner : ReactElement = (<></>);

        switch(this.props.tooltipType){
            case "standard":
                tooltipInner = (<>
                    <div className="app-tool-tip">
                        <h3>{this.props.tooltipStandardContent?.headerText}</h3>
                        <p>{this.props.tooltipStandardContent?.copy}</p>
                    </div>
                </>);

            case "custom":
                tooltipInner = (<>
                    <div className="app-tool-tip" style={{width:"100px", height:"100px"}}>
                        <h2>my custom tooltip</h2>
                        {this.props.toolTipCustomElement}
                    </div>
                </>);
        }

        console.log("tool tip inner",tooltipInner);

        return (
            <div style={{border: "3px solid red", width:"1px", height:"1px", position: "fixed", top : this.state.tooltipPosition.y, left: this.state.tooltipPosition.x}} >
                {tooltipInner}
            </div>
        )
    }



    componentWillUnmount(): void {
        this.clearHover();
        this.clearInterval();
    }

    clearInterval(){
        clearInterval(this.intervalID);
    }

    startHover(){
        this.intervalID = setInterval(()=>this.updateMousePos(), 100);
    }

    clearHover(){
        this.setState({isHovering:false} );
    }

    componentDidMount(): void {
        //this.startHover();
    }

    render(): React.ReactElement<any, string | React.JSXElementConstructor<any>> | string | number | {} | React.ReactNodeArray | React.ReactPortal | boolean | null | undefined {


        let constructedToolTip = (
          <>
              {this.getToolTip()}
               <p style={{position:"fixed", top:0, right:0}}>{`x: ${this.state.tooltipPosition.x} y: ${this.state.tooltipPosition.y} `}</p>
          </>
        );

        return constructedToolTip;
    }

}
