import * as React from "react";

interface iPROPS{
    buttonType: "main-action" | "secondary-action" | "menu-button";
    buttonLabel? : string;
    icon? : any;

}

interface iSTATE{

}

export class AppButton extends React.Component<iPROPS, iSTATE>{
constructor(props:iPROPS) {
    super(props);
}

    getButton(){
        switch (this.props.buttonType) {
            case "main-action":
                return(
                    <button className="btn-main-action">{this.props.buttonLabel}</button>
                );

            case "secondary-action":
                return(
                    <button className="btn-secondary">{this.props.buttonLabel}</button>
                );

            default:
                return(
                    <button>Default Button</button>
                )
        }
    }
    render() {


        return (
            <>
                {this.getButton()}
            </>
        );
    }
}

export default AppButton;
